    public Document loadConfig(String configPath) throws Exception {
        if (configPath == null || configPath.trim().isEmpty()) {
            throw new IllegalArgumentException("配置文件路径不能为空");
        }

        File configFile = new File(configPath);
        if (!configFile.exists() || !configFile.isFile()) {
            throw new java.io.IOException("配置文件不存在或不是一个文件: " + configPath);
        }

        // 一次性读取文件内容到内存中，防止TOCTOU攻击
        byte[] fileContent = readFileContent(configFile);

        // 创建一个没有特定schema的Schema对象，用于基本的XML格式验证
        SchemaFactory schemaFactory = SchemaFactory.newInstance(XMLConstants.W3C_XML_SCHEMA_NS_URI);
        Schema schema = schemaFactory.newSchema(); // 空schema只验证格式
        Validator validator = schema.newValidator();

        // 使用内存中的内容进行验证，防止文件在验证后被替换
        try (InputStream is = new ByteArrayInputStream(fileContent)) {
            validator.validate(new StreamSource(is));
        }

        // 验证通过后，使用相同的内容进行解析返回
        DocumentBuilderFactory dbf = DocumentBuilderFactory.newInstance();

        // 防止XXE攻击的安全配置
        dbf.setFeature(XMLConstants.FEATURE_SECURE_PROCESSING, true);
        dbf.setFeature("http://apache.org/xml/features/disallow-doctype-decl", true);
        dbf.setFeature("http://xml.org/sax/features/external-general-entities", false);
        dbf.setFeature("http://xml.org/sax/features/external-parameter-entities", false);

        // 限制实体引用
        dbf.setXIncludeAware(false);
        dbf.setExpandEntityReferences(false);

        DocumentBuilder db = dbf.newDocumentBuilder();
        try (InputStream is = new ByteArrayInputStream(fileContent)) {
            return db.parse(is);
        }
    }

    /**
     * 读取文件的全部内容到字节数组中 (Java 8兼容的实现)
     * @param file 要读取的文件
     * @return 文件内容的字节数组
     * @throws IOException 读取文件时发生错误
     */
    private byte[] readFileContent(File file) throws IOException {
        try (InputStream inputStream = new FileInputStream(file);
             ByteArrayOutputStream buffer = new ByteArrayOutputStream()) {

            int nRead;
            byte[] data = new byte[8192]; // 8KB缓冲区

            while ((nRead = inputStream.read(data, 0, data.length)) != -1) {
                buffer.write(data, 0, nRead);
            }

            return buffer.toByteArray();
        }
    }
