    private void validateUrl(String url) throws IllegalArgumentException {
        try {
            URI uri = new URI(url);
            String scheme = uri.getScheme();
            String host = uri.getHost();

            // 检查协议是否允许
            if (scheme == null || !ALLOWED_PROTOCOLS.contains(scheme.toLowerCase())) {
                throw new IllegalArgumentException("Unsupported protocol: " + scheme);
            }

            // 检查主机名是否存在
            if (host == null || host.isEmpty()) {
                throw new IllegalArgumentException("Host is required in URL");
            }

            // 检查是否为IP地址并验证是否在禁止范围内
            if (isPrivateIpAddress(host)) {
                throw new IllegalArgumentException("Access to private IP addresses is not allowed");
            }

        } catch (URISyntaxException e) {
            throw new IllegalArgumentException("Invalid URL format: " + url, e);
        }
    }

    private boolean isPrivateIpAddress(String host) {
        // 尝试解析为IP地址
        if (isValidIPv4Address(host)) {
            for (String blockedRange : BLOCKED_IP_RANGES) {
                if (isInCidrRange(host, blockedRange)) {
                    return true;
                }
            }
