        // 如果是localhost或内网域名，也拒绝
        if (host.equalsIgnoreCase("localhost") || 
            host.endsWith(".internal") ||
            host.endsWith(".local")) {
            return true;
        }
        return false;
    }

    private boolean isValidIPv4Address(String ip) {
        String[] parts = ip.split("\\.");
        if (parts.length != 4) {
            return false;
        }
        try {
            for (String part : parts) {
                int num = Integer.parseInt(part);
                if (num < 0 || num > 255) {
                    return false;
                }
            }
            return true;
        } catch (NumberFormatException e) {
            return false;
        }
    }

    private boolean isInCidrRange(String ipAddress, String cidrNotation) {
        String[] parts = cidrNotation.split("/");
        String networkAddress = parts[0];
        int prefixLength = Integer.parseInt(parts[1]);

        try {
            int[] ipParts = Arrays.stream(ipAddress.split("\\."))
                .mapToInt(Integer::parseInt).toArray();
            int[] networkParts = Arrays.stream(networkAddress.split("\\."))
                .mapToInt(Integer::parseInt).toArray();

            // 转换为整数进行比较
            int ipInt = (ipParts[0] << 24) | (ipParts[1] << 16) | (ipParts[2] << 8) | ipParts[3];
            int netInt = (networkParts[0] << 24) | (networkParts[1] << 16) | (networkParts[2] << 8) | networkParts[3];

            // 计算子网掩码
            int mask = prefixLength == 0 ? 0 : (0xFFFFFFFF << (32 - prefixLength));

            return (ipInt & mask) == (netInt & mask);
        } catch (Exception e) {
            return false;
        }
    }
